import React, { useState } from "react";
import AddUser from "./components/Users/AddUser";
import UsersList from "./components/Users/UsersList";
import ErrorModal from "./components/UI/ErrorModal";

function App() {
  const [first, setfirst] = useState([]);
  const [error, setError] = useState();

  const userDataHandler = (uName, uAge) => {
    return setfirst((predata) => {
      return [
        ...predata,
        { username: uName, age: uAge, id: Math.random().toString() },
      ];
    });
  };

  const nameError = (data) => {};

  const ageError = (data) => {};

  return (
    <div>
      <AddUser onSubmit={userDataHandler} />
      <UsersList users={first} />
      {error && <ErrorModal onNameError={nameError} onAgeError={ageError} />}
    </div>
  );
}

export default App;
