  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const logoutHandler = () => {
    setIsLoggedIn(false);
  };

  const loginHandler = () => {
    setIsLoggedIn(true);
  };
  return <AuthContextProvider>{props.children}</AuthContextProvider>;
};

export default AuthContext;
